import React, { useState, useEffect } from 'react';
import './App.css'; // <-- Esto importa los estilos

function App() {
    const [peliculas, setPeliculas] = useState([]);
    const [peliculasFiltradas, setPeliculasFiltradas] = useState([]);
    const [busqueda, setBusqueda] = useState('');
    const [modoDescripcion, setModoDescripcion] = useState(false);
    const [recomendacion, setRecomendacion] = useState('');

    // Cargar películas al iniciar la app
    useEffect(() => {
        fetch('https://recomendaciones-backend-zx6r.onrender.com/api/peliculas')
            .then(res => res.json())
            .then(data => {
                setPeliculas(data);
                setPeliculasFiltradas(data);
            })
            .catch(err => console.error('Error al obtener películas:', err));
    }, []);

    const handleBuscar = (e) => {
        e.preventDefault();
        if (!busqueda) {
            setPeliculasFiltradas(peliculas);
            setRecomendacion('');
            return;
        }
        if (modoDescripcion) {
            handleBuscarPorDescripcion();
        } else {
            const filtradas = peliculas.filter(p =>
                p.titulo.toLowerCase().includes(busqueda.toLowerCase()) ||
                p.genero.toLowerCase().includes(busqueda.toLowerCase())
            );
            setPeliculasFiltradas(filtradas);
            setRecomendacion('');
        }
    };

    const handleBuscarPorDescripcion = async () => {
        try {
            const res = await fetch('https://recomendaciones-backend-zx6r.onrender.com/api/recomendaciones', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    prompt: `Dame una recomendación basada en esta descripción:
${busqueda}. Usa solo películas de este catálogo:
${peliculas.map(p => p.titulo).join(', ')}.`
                })
            });
            const data = await res.json();
            setRecomendacion(data.recomendacion);
            const seleccionadas = peliculas.filter(p =>
                data.recomendacion.toLowerCase().includes(p.titulo.toLowerCase())
            );
            if (seleccionadas.length > 0) {
                setPeliculasFiltradas(seleccionadas);
            }
        } catch (err) {
            console.error('Error con IA:', err);
        }
    };

    return (
        <>
            <h1 className="titulo">Catálogo de Películas</h1>
            <form className="buscador" onSubmit={handleBuscar}>
                <input
                    type="text"
                    placeholder={modoDescripcion ? 'Describe la peli que buscas...' : 'Busca por título o género'}
                    value={busqueda}
                    onChange={e => setBusqueda(e.target.value)}
                />
                <button
                    type="button"
                    className="boton-modo"
                    onClick={() => setModoDescripcion(!modoDescripcion)}
                >
                    {modoDescripcion ? 'Buscar por título/género' : 'Buscar por descripción'}
                </button>
                <button type="submit" className="boton-buscar">Buscar</button>
            </form>

            {recomendacion && (
                <div className="bloque-recomendaciones">
                    <h2>IA sugiere:</h2>
                    <p>{recomendacion}</p>
                </div>
            )}

            <div className="grid">
                {peliculasFiltradas.map((p, i) => (
                    <div className="tarjeta" key={i}>
                        <img src={p.poster} alt={p.titulo} />
                        <div className="info">
                            <h3>{p.titulo}</h3>
                            <p>{p.genero}</p>
                            <span>{p.descripcion?.slice(0, 60)}...</span>
                        </div>
                    </div>
                ))}
            </div>
        </>
    );
}

export default App;
